mapper.update:

update b_reply
set b_reply_like_member =concat_ws(',',b_reply_like_member,#{mno})
where b_reply_num = #{b_reply_num}


update b_reply
set b_reply_like_member = #{b_reply_like_member}
where b_reply_num = #{b_reply_num}


update b_reply
set b_reply_likecount = b_reply_likecount + 1
where b_reply_num = #{b_reply_num}


update b_reply
set b_reply_likecount = b_reply_likecount - 1
where b_reply_num = #{b_reply_num}
