mapper.select:

select *
from board
where board_num = #{boardNum}

mapper.update:

update board
set board_readcount=board_readcount + 1
where board_num = #{boardNum}


update board
set board_likecount= #{board_likecount}
where board_num = #{boardNum}


update board
set board_replycount=board_replycount + 1
where board_num = #{b_board_num}


update board
set board_replycount=board_replycount - 1
where board_num = #{b_board_num}
